    }

    function extractSingleValue(val) {
        if (typeof val === 'string') return val;
        if (val['@value']) return val['@value'];
        if (val['@id']) return val['@id'];
        if (val['foaf:name']) return val['foaf:name'];
        if (val['rdfs:label']) return val['rdfs:label'];
