function escapeString(str) {
    return str.replace(/"/g, '\\"');
}

function toTriple(subject, predicate, object, isUri = false) {
    if (!object) return '';
    if (Array.isArray(object)) {
        return object.map(o => toTriple(subject, predicate, o, isUri)).join('');
    }
    object = isUri ? `<${object}>` : `"${escapeString(object)}"`;
    return `<${subject}> <${predicate}> ${object} .\n`;
}

