const ogcData = msg.payload;

const dcatCatalog = {
    "@type": "dcat:Catalog",
    "@id": "https://ogc-demo.clearly.app/catalog",
    "dct:title": "Open Data Catalogus Gemeente Rotterdam",
    "dct:description": "Deze catalogus bevat datasets van de Gemeente Rotterdam zoals beschikbaar via OGC API.",
    "dct:publisher": {
        "@type": "foaf:Organization",
