        { "@id": "http://publications.europa.eu/resource/authority/language/NLD" }
    ],
    "dcat:dataset": [],
    "hydra:totalItems": ogcData.numberMatched || ogcData.numberReturned || 1,
    "hydra:itemsPerPage": ogcData.numberReturned || 1,
    "hydra:pageIndex": 1,
    "hydra:view": {}
};

// Add pagination if available
const nextLink = ogcData.links?.find(l => l.rel === "next");
if (nextLink) {
    dcatCatalog["hydra:view"] = {
        "@type": "hydra:PartialCollectionView",
        "hydra:next": nextLink.href
    };
}

// Ensure features array exists
let features = [];
if (Array.isArray(ogcData.features)) {
    features = ogcData.features;
} else if (ogcData.type === "Feature") {
    features = [ogcData]; // wrap single Feature
}

// Process features
features.forEach(feature => {
    const props = feature.properties || {};
    const geometry = feature.geometry;
    const links = feature.links || [];

    const dataset = {
        "@type": "dcat:Dataset",
        "@id": links.find(l => l.rel === "self")?.href || `https://example.com/item/${feature.id}`,
        "dct:identifier": feature.id,
        "dct:title": props.title && props.title.trim() !== "" ? props.title : "Naamloze dataset",
        "dct:description": props.description && props.description.trim() !== "" ? props.description : "Geen beschrijving beschikbaar.",
        "dct:publisher": {
            "@type": "foaf:Agent",
            "foaf:name": "Gemeente Rotterdam"
        },
        "dct:catalog": "oup_ogc_records"
    };

    // Geometry â†’ WKT polygon
    if (geometry && geometry.type === "Polygon" && geometry.coordinates) {
        const coords = geometry.coordinates[0].map(pt => `${pt[0]} ${pt[1]}`).join(", ");
        dataset["dct:spatial"] = {
            "@type": "dct:Location",
            "rdfs:label": "Gemeente Rotterdam",
            "locn:geometry": {
                "@type": "geo:Geometry",
                "geo:asWKT": `POLYGON((${coords}))`
            }
        };
    }

    // Distributions
    const distributions = [];

    links.forEach(link => {
        if (link.rel === "self") {
            distributions.push({
                "@type": "dcat:Distribution",
                "dct:title": "GeoJSON download",
                "dcat:accessURL": { "@id": link.href },
                "dct:format": link.type || "application/json+geojson"
            });
        } else if (link.type === "OGC:WFS") {
            distributions.push({
                "@type": "dcat:Distribution",
                "dct:title": link.title || "WFS Service",
                "dcat:accessURL": { "@id": link.href },
                "dct:format": "OGC:WFS"
            });
        } else if (link.type === "OGC:WMS") {
            distributions.push({
                "@type": "dcat:Distribution",
                "dct:title": link.title || "WMS Service",
                "dcat:accessURL": { "@id": link.href },
                "dct:format": "OGC:WMS"
            });
        }
    });

    if (distributions.length > 0) {
        dataset["dcat:distribution"] = distributions;
    }

    dcatCatalog["dcat:dataset"].push(dataset);
});

